package com.atlassian.oai.validator.mockmvc;import org.junit.Before;import org.junit.Test;import org.springframework.http.MediaType;import org.springframework.test.web.servlet.MockMvc;import org.springframework.test.web.servlet.setup.MockMvcBuilders;import java.nio.charset.StandardCharsets;import static com.atlassian.oai.validator.mockmvc.OpenApiValidationMatchers.openApi;import static io.restassured.module.mockmvc.RestAssuredMockMvc.given;public class MockMvcRestAssuredTest {    private MockMvc mvc;    @Before    public void setup() {        final TestController testController = new TestController();        mvc = MockMvcBuilders.standaloneSetup(testController).build();    }    @Test    public void canUseRestAssuredWithMockMvcAndBodyAsString() {        given().mockMvc(mvc)                .contentType(MediaType.APPLICATION_JSON_VALUE)                .body("{\"id\":123, \"name\":\"newbob\"}")                .when()                .post("/add")                .then()                .expect(openApi().isValid("api.json"));    }    @Test    public void canUseRestAssuredWithMockMvcAndBodyAsByteArray() {        given().mockMvc(mvc)                .contentType(MediaType.APPLICATION_JSON_VALUE)                .body("{\"id\":123, \"name\":\"newbob\"}".getBytes(StandardCharsets.UTF_8))                .when()                .post("/add")                .then()                .expect(openApi().isValid("api.json"));    }}